import React, { Component } from "react";
import { API_URL_SHARP } from "../constants/index"; // Import the weather API URL

class Header extends Component {
  constructor(props) {
    super(props);
    this.state = {
      weather: null, // State to hold weather data
    };
  }

  componentDidMount() {
    // Use the imported WEATHER_API_URL for the fetch request
    fetch(API_URL_SHARP)
      .then((response) => response.json())
      .then((data) => {
        this.setState({ weather: data[0] }); // Assuming the API returns an array of weather data
      })
      .catch((error) => console.error("Error fetching weather data:", error));
  }

  render() {
    const { weather } = this.state;
    return (
      <div className="text-center">
        <img
          src="https://logrocket-assets.io/img/logo.png"
          width="300"
          className="img-thumbnail"
          style={{ marginTop: "20px" }}
        />
        <hr />
        <h5>
          <i>presents</i>
        </h5>
        <h1>App with React + Django</h1>

        {/* Display weather info if available */}
        {weather ? (
          <div>
            <p>{weather.date}</p>
            <p>Temperature: {weather.temperatureC}°C ({weather.temperatureF}°F)</p>
            <p>Summary: {weather.summary}</p>
          </div>
        ) : (
          <p>Loading weather data...</p>
        )}
      </div>
    );
  }
}

export default Header;
